const Filter = () => {
    return (
        <div>
            <label htmlFor="filter">Find contacts by name</label>
              <input
                type="text"
                name="filter"
                placeholder="Search by name"
                value={this.state.filter}
                onChange={this.handleFilterChange}
            />
        </div>
    )
}

export default Filter;
