const Filter = ({filter, handleFilter}) => {
    return (
        <div>
            <label htmlFor="filter">Find contacts by name</label>
              <input
                type="text"
                name="filter"
                placeholder="Search by name"
                value={filter}
                onChange={(e) => handleFilter}
            />
        </div>
    )
}

export default Filter;
