import React, { Component } from "react";
import "./phoneBook/phonebook.css";


export class App extends Component{
  state = {
    contacts: [
      {id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
      {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
      {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
      {id: 'id-4', name: 'Annie Copeland', number: '227-91-26'},
    ],
    filter: '',
    name: '',
    number: '',
  }

  submitForm = evt => {
    evt.preventDefault();
    const form = evt.currentTarget;
    const name = form.elements.name.value;
    const number = form.elements.number.value;
    console.log(name);
    if (name === '' || number === '') {
      return;
    }
    this.setState({
      name: name,
      number: number,
    }, () => {
      const newCont = {name, number};
      this.setState({
        contacts: [...this.state.contacts, newCont],
      }, () => {
        console.log(this.state.contacts);
      });
    });
  }

  createContact = () => {
    const filteredContacts = this.state.contacts.filter(contact =>
      contact.name.toLowerCase().includes(this.state.filter.toLowerCase())
    );

    return filteredContacts.map((contact, index) => {
      return <li key={index}>{contact.name}: {contact.number} <button className="delete">Delete</button></li>
    });
  }

  handleFilterChange = evt => {
    const filterValue = evt.target.value;
    this.setState({
      filter: filterValue
    });
  };

  render() {

    return (
      <div className="main">
        <h1>Phonebook</h1>
        <form onSubmit={this.submitForm} className="form">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            name="name"
            className="input-name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
            required
          />
          <label htmlFor="number">Phone Number</label>
          <input
            type="tel"
            name="number"
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
            required
          />
          <button type="submit" className="submit">Add contact</button>
        </form>

        <div>
          <h2>Contacts</h2>
          <input
            type="text"
            placeholder="Search by name"
            value={this.state.filter}
            onChange={this.handleFilterChange}
          />

          <ul>
            {this.createContact()}
          </ul>
        </div>
      </div>
    )
  }
}
